import React from "react";

function SearchResult(props) {
    const card={
        width:'18rem',
        boxShadow:'3px 3px 3px 3px #888888'
    }
    const img={
       margin:'0px'
    }
    const profile={
        border:"2px solid black"
    }
    return (
        <div class="card" style={card}>
            <div class="card-body d-flex flex-column">
                <div>
                <img style={img} src="https://mdbootstrap.com/img/Photos/Avatars/avatar-8.jpg" class="rounded-circle mr-3" height="170px" width="170px" alt="avatar" />
                </div>
                <div style={profile}>
                    <h4 class="card-title font-weight-bold mb-2">{props.result}</h4>
                    <button type="button" class="btn btn-primary ">Follow</button>
                </div>
            </div>
        </div>


    );
}

export default SearchResult;
