import React, { useState } from "react";
import SearchBox from './SearchBox';
import SearchResult from './SearchResult';
import axios from 'axios';
function SearchPage(props) {
  const [result, setResult] = useState('');
  const [display,setDisplay] = useState(false);

  const style = {
    SearchBox: {
      border: "0px solid Black"
    },
    SearchResult: {
      border: "0px solid Yellow"
    }
  }
  const [input, setInput] = useState('');
  const [namesList, setnamesList] = useState([]);

  async function handleChange(e) {
    const i = e.target.value;
    console.log(i);

    // const input = e.target.value;
    const userInfo = await axios.get('/api/allusers')
    const userdata = userInfo.data;
    console.log(userdata);
    if (i.length >= 1 || i.length <= 2) {
      userdata.forEach(function (item) {
        console.log(`[name received]`,item.firstName);
        namesList.push(item.firstName);
      });
    }
    console.log(namesList);

    if (i.length > 1 ){
      console.log(`entering this loop`)
      const newList = namesList.filter(name => name.indexOf(i)==0);
      console.log('[new list received ]',newList)
      setInput(i);
      setnamesList(newList)
    } else {
      setInput(i);
      setnamesList([]);
    }

  }


  async function search() {
    console.log(`[input Received]`,input);
    const user = await axios.get(`/api/user/${input}`)
    console.log(`[user received ]`,user);
    setResult(user.data[0].firstName)
    setDisplay(true);
  }

if (display){
  return (
    <>
      <div class="d-flex flex-column h-100">
        <form class="form-inline md-form mr-auto mb-4">
          <input class="form-control mr-sm-2" type="text" onChange={handleChange} placeholder="Search" value={input} aria-label="Search" />
          <button class="btn aqua-gradient btn-rounded btn-sm my-0" onClick={search} type="button">Search</button>
        </form>
        <ul>{namesList.map(name => <li class="list-group-item" onClick={() => setInput(name)}>{name}</li>)}</ul>
        <div>
          <SearchResult result={result} />
        </div>
      </div>
    </>
  )
}
else{

  return (
    <>
      <div class="d-flex flex-column h-100">
        <form class="form-inline md-form mr-auto mb-4">
          <input class="form-control mr-sm-2" type="text" onChange={handleChange} placeholder="Search" value={input} aria-label="Search" />
          <button class="btn aqua-gradient btn-rounded btn-sm my-0" onClick={search} type="button">Search</button>
        </form>
        <ul>{namesList.map(name => <li class="list-group-item" onClick={() => setInput(name)}>{name}</li>)}</ul>
      </div>
    </>
  )
  }
}
export default SearchPage;
