import React from 'react';

function Goals() {
    const [show,setShow] = useState(false);
    const card={
        width:'100%',
        margin:'10px',
        marginTop: '30px'
    }

    const selectOption = {
        display:'block',
        marginLeft:'auto',
        marginRight:'auto'
    }
    async function addGoal(){
        console.log('[Add New GOAL button pressed]',show)
        setShow(true);
    }
    async function closeGoal(){
        setShow(false);
    }
    return (
        <div class="card" style={card}>
            <div class="card-body">
                <button class='btn btn-light' onClick={addGoal} style={selectOption}><i class="fas fa-plus"></i>   Add New Goal</button>
                <br></br>
                <button class='btn btn-light' style={selectOption}><i class="fas fa-users"></i>   Followers</button>
                <br></br>
                <button class='btn btn-light' style={selectOption}><i class="fas fa-users"></i>   Following</button>
            </div>
        </div>
    );
}

export default Goals;
