import React,{useState} from "react";
import Posts from './Posts';
import Goals from './Goals';
import Comments from './Comments';
import GoalModal from './GoalModal';

function HomePage(){
   const home = {
       width:'100%',
       height:'50px',
       textAlign:'center',
       backgroundColor:'rgb(230, 126, 34)',
       color:'white',
       fontFamily: "'Noto Sans', sans-serif",
       borderStyle:'groove'
   }
   const liveData = {
       flex:1,
       flexDirection:'row',
       padding:'10px',
       margin:'0px'

   }
   const columns = {
       padding:'0',
       margin:'4px'
   }
   const newGoal = {
    display:'block',
    marginLeft:'auto',
    marginRight:'auto'
}
const [display,setDisplay] = useState(true);
async function addGoal(){
    setDisplay(true);
}
async function closeGoalForm(){
    setDisplay(false);
}
   if(display){
    return (
        <div>
        <div>
            <h3  style={home}>Home Page</h3>
        </div>
        <div class='row' style={liveData}>
            <div class='col-12 col-sm-12 col-md-7' style={columns}>
            <Posts />
            </div>
            <div class='col-12 col-sm-12 col-md-4'style={columns} >
            <Goals />
            <Comments />
            <button class='btn btn-light' onClick={addGoal} style={newGoal}><i class="fas fa-plus"></i>   Add New Goal</button>
            <GoalModal closeGoalForm={closeGoalForm} />
            </div>
        </div>

        </div>
    );}
    else{
        return(
            <div>
        <div>
            <h3  style={home}>Home Page</h3>
        </div>
        <div class='row' style={liveData}>
            <div class='col-12 col-sm-12 col-md-7' style={columns}>
            <Posts />
            </div>
            <div class='col-12 col-sm-12 col-md-4'style={columns} >
            <Goals />
            <button class='btn btn-light' onClick={addGoal} style={newGoal}><i class="fas fa-plus"></i>   Add New Goal</button>
            <Comments />
            </div>
        </div>

        </div>
        );
    }

}

export default HomePage;
